func TestExtractLocations(t *testing.T) {
	locations := iter.ToSlice(IndexAll("test that this returns a match", "test"))
	if locations[0][0] != 0 {
		t.Error("Expected to find location 0")
	}
}

func TestExtractLocationsLarge(t *testing.T) {
	locations := iter.ToSlice(IndexAll("1234567890123456789012345678901234567890123456789012345678901234567890123456789012345678901234567890 test that this returns a match", "test"))

	if locations[0][0] != 101 {
		t.Error("Expected to find location 101")
	}
}

func TestExtractLocationsLimit(t *testing.T) {
	assert.Equal(t, 1, iter.Count(iter.Take(IndexAll("test test", "test"), 1)))
}

func TestExtractLocationsLimitTwo(t *testing.T) {
	assert.Equal(t, 2, iter.Count(iter.Take(IndexAll("test test test", "test"), 2)))
}

func TestExtractLocationsLimitThree(t *testing.T) {
	assert.Equal(t, 3, iter.Count(iter.Take(IndexAll("test test test", "test"), 3)))
}

func TestExtractLocationsNegativeLimit(t *testing.T) {
	assert.Equal(t, 3, iter.Count(IndexAll("test test test", "test")))
}

func TestDropInReplacement(t *testing.T) {
	r := regexp.MustCompile(`test`)

	matches1 := r.FindAllIndex([]byte(testMatchEndCase), -1)
	matches2 := iter.ToSlice(IndexAll(testMatchEndCase, "test"))

	for i := 0; i < len(matches1); i++ {
		if matches1[i][0] != matches2[i][0] || matches1[i][1] != matches2[i][1] {
			t.Error("Expect results to match", i)
		}
	}
}

func TestDropInReplacementNil(t *testing.T) {
	assert.Nil(t, regexp.
		MustCompile(`test`).
		FindAllIndex([]byte(`aaaaaaaaaaaaaaaaaaaaaaaaaaaaaa`), -1))

	assert.Equal(t, 0, iter.Count(IndexAll(`aaaaaaaaaaaaaaaaaaaaaaaaaaaaaa`, "test")))
}

func TestDropInReplacementMultiple(t *testing.T) {
	r := regexp.MustCompile(`1`)

	matches1 := r.FindAllIndex([]byte(`111`), -1)
	matches2 := iter.ToSlice(IndexAll(`111`, "1"))

	for i := 0; i < len(matches1); i++ {
		if matches1[i][0] != matches2[i][0] || matches1[i][1] != matches2[i][1] {
			t.Error("Expect results to match", i)
		}
	}
}

