func TestIndexAllIgnoreCaseUnicodeCheck(t *testing.T) {
	matches := iter.ToSlice(IndexAllIgnoreCase("a secret a", "ſecret"))
	assert.Equal(t, [][2]int{{2, 8}}, matches)
}

func TestIndexAllIgnoreCaseUnicodeCheckEnd(t *testing.T) {
	matches := iter.ToSlice(IndexAllIgnoreCase("a ſecret a", "secret"))
	assert.Equal(t, [][2]int{{2, 9}}, matches)
}

func TestDropInReplacementMultipleIndexAllIgnoreCaseUnicode(t *testing.T) {
	r := regexp.MustCompile(`1`)

	matches1 := r.FindAllIndex([]byte(`111`), -1)
	matches2 := iter.ToSlice(IndexAllIgnoreCase(`111`, "1"))

	for i, match := range matches1 {
		assert.Equal(t, [2]int{match[0], match[1]}, matches2[i])
	}
}

func TestIndexAllIgnoreCaseUnicodeSpace(t *testing.T) {
	r := regexp.MustCompile(`(?i)ten thousand a year`)
	index := r.FindAllIndex([]byte(prideAndPrejudice), -1)

	matches := iter.Count(IndexAllIgnoreCase(prideAndPrejudice, "ten thousand a year"))
	assert.Equal(t, len(index), matches)

	m := iter.Count(IndexAll(strings.ToLower(prideAndPrejudice), "ten thousand a year"))
	assert.Equal(t, len(index), m)
}

func TestIndexAllIgnoreCaseAtEnd(t *testing.T) {
	assert.Equal(t, [][2]int{{4, 8}}, iter.ToSlice(IndexAllIgnoreCase(`testjava`, "java")))

	r := regexp.MustCompile(`java`)
	assert.Equal(t, [][]int{{4, 8}}, r.FindAllIndex([]byte(`testjava`), -1))
}

func TestIndexAllIgnoreCaseStrange(t *testing.T) {
	const haystack = `func AllSimpleFold(input rune) []rune {
        res := []rune{}
`
	const needle = "rune{}"
	matches := iter.ToSlice(IndexAllIgnoreCase(haystack, needle))

	r := regexp.MustCompile(needle)
	index := r.FindAllIndex([]byte(haystack), -1)

	require.Equal(t, len(index), len(matches))
	assert.Equal(t, 57, matches[0][0])
	assert.Equal(t, 63, matches[0][1])
}

func TestIndexAllIgnoreCaseStrangeTwo(t *testing.T) {
	matches := iter.ToSlice(IndexAllIgnoreCase(`this is my cs ß haystack`, `ß`))
	r := regexp.MustCompile(`ß`)
	index := r.FindAllIndex([]byte(`this is my cs ß haystack`), -1)

	if len(matches) != len(index) || matches[0][0] != 14 || matches[0][1] != 16 {
		t.Error("Expected 1 got", len(matches))
	}
}

