// When we limit we want the searches to be in order in order to match what FindAllIndex would do
// as closely as possible
func TestIndexAllIgnoreCaseLimitSmallNeedle(t *testing.T) {
	// matches := iter.ToSlice(iter.Take(IndexAllIgnoreCase("Test TEST test tEST", "te"), 2))
	matches := iter.ToSlice(IndexAllIgnoreCase("Test TEST test tEST", "te"))
	assert.Equal(t, [][2]int{
		{0, 2},
		{5, 7},
		{10, 12},
		{15, 17},
	}, matches)
}

// When we limit we want the searches to be in order in order to match what FindAllIndex would do
// as closely as possible
func TestIndexAllIgnoreCaseLimitLargeNeedle(t *testing.T) {
	matches := iter.ToSlice(iter.Take(IndexAllIgnoreCase("Test TEST test tEST", "test"), 2))
	assert.Equal(t, [][2]int{
		{0, 4},
		{5, 9},
	}, matches)
}

func TestIndexesAllIgnoreCaseUnicodeCaseInsensitive(t *testing.T) {
	matches := IndexAllIgnoreCase(testUnicodeMatchEndCase, "test")
	assert.Equal(t, 1, iter.Count(matches))
}
